import React , { useEffect , useState } from "react";
import axios from 'axios'
import stl from '../../styles/reviews.module.css'


export default ({ id , APIkey }) => {
    const [ reviews , setReviews ] = useState(null)

    useEffect(() => {
        async function fetchData() {
            const { data } = await axios.get(`https://api.themoviedb.org/3/movie/${id}/reviews?api_key=${APIkey}`)
            setReviews(data)
          }
        fetchData()
    }, [])

    return (
        <ul className = {stl.wrapperReviews}>
       {reviews && reviews.results.map(({ id , author , content }) => (
            <li key = {id}>
               <h3>{author}</h3>
               <p>{content}</p>
            </li>
       ))}
       {reviews && !reviews.results.length && <h3>no reviews</h3>}
          </ul>
        )
}
