import React , { useEffect , useState } from "react";
import axios from 'axios'
import stl from '../../styles/cast.module.css'


export default ({ id , APIkey }) => {
    const [ cast , setCast ] = useState(null)

    useEffect(() => {
        async function fetchData() {
            const { data } = await axios.get(`https://api.themoviedb.org/3/movie/${id}/credits?api_key=${APIkey}`)
            setCast(data)
          }
        fetchData()
    }, [])

    console.log(cast)

    return (
        <>
        {cast &&
            <ul className = {stl.castWrapper}>
            {cast.cast.map(({ id , profile_path , name , character }) => (
                <li key = {id}>
                    <span>{name}</span>
                    <img alt="cast-pic" src={`https://image.tmdb.org/t/p/original${profile_path}`}></img>
                    <span>Character: {character}</span>
                </li>
            ))}
            </ul> 
        }
          </>
        )
      
}
